.about-me {
  @include container;
  @include clearfix;

  margin-top: 5em;
  margin-bottom: 5em;
}

.about-img {
  max-height: 25em;
  border-radius: 50%;

  @media (min-width: 42em) {
    float: left;
    margin-right: 1em;
  }
}

.about-title {
  margin-bottom: .25em;  
}

.about-subtitle {
  margin: .5em 0;
}

@supports (display: grid) {
  .about-me {
    display: grid;
    grid-template-columns: [port-start] minmax(6em, 1fr) [name-start] minmax(6em, 1fr) [img-end] 2fr [port-end];
    grid-column-gap: .5em;
  }

  .about-img {
    grid-column: port / img;
    grid-row: 1 / 2;

    @include mq {
      grid-row: 1 / 4;
    }
  }

  .about-title {
    grid-column: name / port;
    grid-row: 1 / 2;
    align-self: end;
  }

  .about-subtitle,
  .about-text {
    grid-column: port / port;

    @include mq {
      grid-column: img-end / port-end;
    }
  }


  .about-subtitle {
    grid-row: 2 / 3;
  }

  .about-text {
    grid-row: 3 / 4;
  }
}

sources.scss\\partials\\_portfolio.scss:
.portfolio {
  text-align: center;
  background: $light-gray;
  padding: 5em 0;
}

.portfolio-items {
  display: flex;
  flex-wrap: wrap;
  @include container($max-width: 75em);
}

.portfolio-item {
  position: relative;
  overflow: hidden;
  flex: 1 1 20rem;

  .portfolio-image {
    display: block;
    width: 100%;
  }

  figcaption {
    position: absolute;
    top: 100%;
    bottom: 0;
    right: 0;
    left: 0;
    padding: 1em;
    background: rgba($accent, .75);
    color: $white;
    text-align: left;
    display: flex;
    flex-direction: column;
    transition: top ease-in-out 250ms;
  }

  &:hover figcaption,
  &:focus figcaption {
    top: 0;
  }
}

.portfolio-title {
  color: $white;
  font-size: 2.5rem;
  margin-bottom: auto;
}

.portfolio-desc {
  display: none;
}

.portfolio-link {
  color: white;
  font-weight: $fw-b;
  font-size: 1.1rem;
  text-decoration: none;
  border-bottom: 3px solid $white;
  align-self: flex-start;

  &::after {
    content: url(../img/arrow-white.svg);
    display: inline-block;
    height: .8em;
    width: .8em;
    margin-left: .5em;
  }
}

@supports (display: grid) {
  .portfolio-items {
    display: grid;
    grid-template-columns: repeat(2, 1fr);

    @include mq {
      grid-template-columns: repeat(4, 1fr);
    }
  }

  .portfolio-item.featured {
    grid-column: 1 / span 2;
    grid-row: 1 / span 2;
  }

  .featured{
    .portfolio-title {
      font-size: 5rem;
    }


    .portfolio-desc {
      display: block;
    }
  }
}

.portfolio-modal {
  position: fixed;
  z-index: 10;
  top: 2em;
  bottom: 2em;
  left: 2em;
  right: 2em;
  background: white;
  box-shadow: 0 0 0 3em rgba(black, .5);
  overflow-y: scroll;
  overflow-x: hidden;

  display: none;

  &.is-open {
    display: block;
  }
}

.modal-close { 
  background: white;
  position: fixed;
  left: 1em;
  top: 1em;
  border: 0;
  width: 2em;
  height: 2em;
  padding: 4px 0 0;
  border-radius: 50%;
  cursor: pointer;
  box-shadow: 0 0 5px rgba(black, .5);
}

.portfolio-header {
  text-align: left;
  padding: 5em 0;
  margin-bottom: 2em;
  background-size: cover;
  background-position: center;
}

.header-one {
  background-image: url(../img/portfolio-01.jpg);
}

.header-two {
  background-image: url(../img/portfolio-02.jpg);
}

.portfolio-title-box {
  background: rgba($accent, .75);
  padding: 3em 0 3em 3em;
  color: white;
  width: 70%;

  .portfolio-title {
    font-size: 3.5rem;
    display: inline-block;

    &::after {
      content: '';
      display: block;
      height: 1px;
      margin: .5em 0;
      background: white;
      width: 100%;
    }
  }
}

.portfolio-subtitle {
  font-size: 1rem;
}

.portfolio-content {
  text-align: left;
  width: 85%;
  margin: 0 auto;
  padding-bottom: 2em;
}

.portfolio-full-width {
  width: 100vw;
  max-width: initial;
  margin: 2em calc(50% - 50vw);
  height: 20vh;
  object-fit: cover;
}















@include mq {
  .portfolio-pull-left {
    width: 65%;
    float: left;
    margin-right: 2em;
  }

  .portfolio-pull-right {
    width: 65%;
    float: right;
    margin-left: 2em;
  }
}

@keyframes modalIn {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

@keyframes modalOut {
  0% { opacity: 1; }
  100% { opacity: 0; }
}

